import tw, { styled } from 'twin.macro';
import { useEffect, useState } from 'react';
import { BrowserWallet } from '@martifylabs/mesh';
import { useWallet } from '@mesh/hooks';
import { MenuItem } from './MenuItem';
import { WalletBalance } from './WalletBalance';
import type { Wallet } from '@martifylabs/mesh';

const StyledMenuButton = tw.button`
  flex items-center justify-center
  font-normal text-lg
  border rounded-t-md
  w-60 px-4 py-2
  shadow-sm
`;

const StyledMenuList = styled.div(({ hidden }: { hidden: boolean }) => [
  tw`
    shadow-sm backdrop-blur
    border rounded-b-md
    absolute w-60
    text-center
  `,
  hidden && tw`hidden`,
]);

export const CardanoWallet = () => {
  const [hideMenuList, setHideMenuList] = useState(true);

  const { connect, connected, name, connecting } = useWallet();

  const [wallets, setWallets] = useState<Wallet[]>([]);

  useEffect(() => {
    setWallets(BrowserWallet.getInstalledWallets());
  }, [])

  return (
    <div
      onMouseEnter={() => setHideMenuList(false)}
      onMouseLeave={() => setHideMenuList(true)}
    >
      <StyledMenuButton
        type="button"
        onClick={() => setHideMenuList(!hideMenuList)}
      >
        <WalletBalance
          connected={connected}
          name={name}
          connecting={connecting}
        />
      </StyledMenuButton>
      <StyledMenuList hidden={hideMenuList}>
        {wallets.length > 0 ? (
          wallets.map((wallet, index) => (
            <MenuItem
              key={index}
              icon={wallet.icon}
              name={wallet.name}
              connect={() => {
                connect(wallet.name);
                setHideMenuList(!hideMenuList);
              }}
              active={name === wallet.name}
            />
          ))
        ) : (
          <span>No Wallet Found.</span>
        )}
      </StyledMenuList>
    </div>
  );
};
